        object outObject;
        if (!_instance.TryGetValue(typeof(VALUE).Name, out outObject)) {
            value = default;
            return false;
        }
        Dictionary<KEY, VALUE> dictionary = (Dictionary<KEY, VALUE>)outObject;
        if (!dictionary.TryGetValue(key, out value)) {
            return false;
        }
        return true;
    }
}

